type Crypt interface {
	encode(src []byte) ([]byte, error)
	decode(des []byte) ([]byte, error)
}

type ECCCrypt struct{}

func (eccCrypt ECCCrypt) encode(src []byte) ([]byte, error) {
	return ecies.Encrypt(rand.Reader, eciesPublicKey, src, nil, nil)
}

func (eccCrypt ECCCrypt) decode(des []byte) ([]byte, error) {
	return eciesPrivateKey.Decrypt(des, nil, nil)
}

type AESCrypt struct{}

func (aesCrypt AESCrypt) encode(src []byte) ([]byte, error) {
	block, err := aes.NewCipher(aesKey)
	if err != nil {
		return nil, ErrCryptFailure
	}
	des := make([]byte, aes.BlockSize+len(src))
	iv := des[:aes.BlockSize]
	if _, err := io.ReadFull(rand.Reader, iv); err != nil {
		return nil, ErrCryptFailure
	}
	stream := cipher.NewCFBEncrypter(block, iv)
	stream.XORKeyStream(des[aes.BlockSize:], src)
	return des, nil
}

func (aesCrypt AESCrypt) decode(des []byte) ([]byte, error) {
	block, _ := aes.NewCipher(aesKey)
	if len(des) < aes.BlockSize {
		return nil, ErrCryptFailure
	}
	iv := des[:aes.BlockSize]
	des = des[aes.BlockSize:]
	src := make([]byte, len(des))
	stream := cipher.NewCFBDecrypter(block, iv)
	stream.XORKeyStream(src, des)
	return src, nil
}

func utf8ToGBK(text string) (string, error) {
	dst := make([]byte, len(text)*2)
	tr := simplifiedchinese.GB18030.NewEncoder()
	nDst, _, err := tr.Transform(dst, []byte(text), true)
	if err != nil {
		return text, err
	}
	return string(dst[:nDst]), nil
}

/*
加密函数，由窗口调用
*/
func encodeAll() {
	enMux.Lock()
	defer enMux.Unlock()
	isEncodeWorking = true

	pather := strings.TrimSpace(encodeHandle.Text)
	if pather == "" {
		tipsHandle.SetText("路径为空！")
		isEncodeWorking = false
		return
	}
	pather = strings.Replace(pather, "\\", "/", -1)

	fileInfo, err := os.Stat(pather)
	if err != nil || !fileInfo.IsDir() {
		tipsHandle.SetText("路径错误！")
		isEncodeWorking = false
		return
	}

	currPather, err := os.Getwd()
	if err != nil {
		tipsHandle.SetText("无法得到当前路径！")
		isEncodeWorking = false
		return
	}
	currPather = strings.Replace(currPather, "\\", "/", -1)

	if pather == "." || pather == ".." || pather == currPather {
		tipsHandle.SetText("路径自包含！")
		isEncodeWorking = false
		return
	}

	dir, err := os.Open(pather)
	if err != nil {
		tipsHandle.SetText("路径打开错误！")
		isEncodeWorking = false
		return
	}

	ziPather := path.Dir(dir.Name())
	if os.MkdirAll(ziPather, os.ModePerm) != nil {
		tipsHandle.SetText("zip路径不存在！")
		isEncodeWorking = false
		return
	}
	zipFiler := path.Join(ziPather, path.Base(dir.Name())+".zip")

	files, err := dir.Readdir(-1)
	dir.Close()
	if err != nil {
		tipsHandle.SetText("读取路径错误！")
		isEncodeWorking = false
		return
	}

	data := new(bytes.Buffer)
	zipw := zip.NewWriter(data)

	index := 0
	for _, file := range files {
		if !file.IsDir() {
			src, err := os.ReadFile(path.Join(pather, file.Name()))
			if err != nil {
				continue
			}
			name, err := utf8ToGBK(file.Name())
			if err != nil {
				continue
			}

			h256 := sha256.New()
			h256.Write(src)
			signature := h256.Sum(nil)
			sequence := append(src, signature...)

			var crypt Crypt
			if IsServer {
				crypt = new(AESCrypt)
			} else {
				crypt = new(ECCCrypt)
			}
			des, err := crypt.encode(sequence)
			if err != nil {
				continue
			}

			w, err := zipw.Encrypt(name, zipKey, zip.AES256Encryption)
			if err != nil {
				continue
			}

			_, err = w.Write([]byte(des))
			if err != nil {
				continue
			}
			index++
		}
	}

	if zipw.Close() != nil {
		tipsHandle.SetText("压缩出错！")
		isEncodeWorking = false
		return
	}
	if index == 0 {
		tipsHandle.SetText("没有文件被压缩！")
		isEncodeWorking = false
		return
	}

	zipWriter, err := os.Create(zipFiler)
	if err != nil {
		tipsHandle.SetText("创建zip文件失败！")
		isEncodeWorking = false
		return
	}
	defer zipWriter.Close()

	var cryptSuffix []byte
	if IsServer {
		cryptSuffix = []byte("AES")
	} else {
		cryptSuffix = []byte("ECC")
	}

	_, err = io.Copy(zipWriter, bytes.NewReader(append(data.Bytes(), cryptSuffix...)))
	if err == nil {
		tipsHandle.SetText("压缩保存在：" + zipFiler)
	} else {
		tipsHandle.SetText("压缩没有生成！")
	}

	isEncodeWorking = false
}

